import React, { Component } from 'react';

// Normalizes string as a slug - a string that is safe to use
// in both URLs and html attributes
import slugify from 'slugify';

import './App.css';
import NavBar from './Components/NavBar';
import CustomizationList from './Components/CustomizationList'
import Cart from './Components/Cart'

// This object will allow us to
// easily convert numbers into US dollar values
const USCurrencyFormat = new Intl.NumberFormat('en-US', {
  style: 'currency',
  currency: 'USD'
});

class App extends Component {
  state = {
    selected: {
      Processor: {
        name: '17th Generation Intel Core HB (7 Core with donut spare)',
        cost: 700
      },
      'Operating System': {
        name: 'Ubuntu Linux 16.04',
        cost: 200
      },
      'Video Card': {
        name: 'Toyota Corolla 1.5v',
        cost: 1150.98
      },
      Display: {
        name: '15.6" UHD (3840 x 2160) 60Hz Bright Lights and Knobs',
        cost: 1500
      }
    }
  };

  updateFeature = (feature, newValue) => {
    const selected = Object.assign({}, this.state.selected);
    selected[feature] = newValue;
    this.setState({
      selected
    });
  };

  render() {


    return (
      <div className="App">
        <NavBar />
        <main>
          <CustomizationList features={this.props.features} currency={USCurrencyFormat} updateFeature={this.updateFeature} selected={this.state.selected} />
          <section className="main__summary">
            <h2>Your cart</h2>
            <Cart features={this.props.features} selected={this.state.selected} currency={USCurrencyFormat} />

          </section>

        </main>
      </div>
    );
  }
}

export default App;
